import { Form, Button, Card, App, Modal } from "react-bootstrap";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import React from "react";
import LocalStorageService from "../../../service/LocalStorageService";
import axios from "axios";
import Dashboard from "../../Dashboard";
import ForgotPasswordForm from "./ForgotPasswordForm";

export default function LoginForm(props) {

  const [show, setShow] = useState(false);
  const [email, showEmail] = useState(true);
  const [newPasswordForm, showNewPasswordForm] = useState(false);
  const [verification, showVerification] = useState(false);
  const [credentials, setCredentials] = useState({});
  const history = useHistory();

  const handleChange = (event) => {
    setCredentials({
      ...credentials,
      [event.target.name]: event.target.value,
    });
  };

  const verifyCode=()=>{
    showNewPasswordForm(true);
    showVerification(false);

  }

  const changePassword=()=>{

  }

  const handleClose = () => {
    setShow(false);
  };



  const signIn = async () => {
    let path = `home`;
    history.push(path);
  };

  const handleEmail = () => {
    showEmail(false);
    showVerification(true);
  };
  return (
    <Form>
      <div>
        <label
          className="display-6 m-2 justify-content-center"
          style={{ alignContent: "auto", color: "GrayText" }}
        >
          LOGIN
        </label>
      </div>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Id</Form.Label>
        <Form.Control
          type="id"
          placeholder="Enter your id"
          name="id"
          //onChange={handleChange()}
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword" hidden={true}>
        <Form.Label>Password</Form.Label>
        <Form.Control
          type="password"
          placeholder="Password"
          name="password"
          //onChange={handleChange()}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicCheckbox">
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group>
      <div className="d-flex justify-content-between">
        <div className="col-6">
          <Card
            className="button p-2"
            style={{ backgroundColor: "#a3a9f5" }}
            type="submit"
            onClick={signIn}
          >
            Submit
          </Card>
        </div>
        <div className="col-6">
          <Card
            className=" button  ml-auto p-2"
            style={{ backgroundColor: "#a3a9f5" }}
            type="button"
            data-toggle="modal"
            data-target="#exampleModalCenter"
            onClick={handleShow}
          >
            Forgot Password
          </Card>
          <ForgotPasswordForm show = {show}/>
        </div>
      </div>
    </Form>
  );
}
