export type SequentialColorInterpolatorId = keyof typeof sequentialColorInterpolators
import {
    categoricalColorSchemes,
    categoricalColorSchemeIds,
    CategoricalColorSchemeId,
} from './categorical'
import { divergingColorSchemes, divergingColorSchemeIds, DivergingColorSchemeId } from './diverging'
import {
    sequentialColorSchemes,
    sequentialColorSchemeIds,
    SequentialColorSchemeId,
} from './sequential'

export const colorSchemes = {
    ...categoricalColorSchemes,
    ...divergingColorSchemes,
    ...sequentialColorSchemes,
}

export type ColorSchemeId =
    | CategoricalColorSchemeId
    | DivergingColorSchemeId
    | SequentialColorSchemeId

export const colorSchemeIds = Object.keys(colorSchemes) as ColorSchemeId[]

export const isCategoricalColorScheme = (
    scheme: ColorSchemeId
): scheme is CategoricalColorSchemeId =>
    categoricalColorSchemeIds.includes(scheme as CategoricalColorSchemeId)

export const isDivergingColorScheme = (scheme: ColorSchemeId): scheme is DivergingColorSchemeId =>
    divergingColorSchemeIds.includes(scheme as DivergingColorSchemeId)

export const isSequentialColorScheme = (scheme: ColorSchemeId): scheme is SequentialColorSchemeId =>
    sequentialColorSchemeIds.includes(scheme as SequentialColorSchemeId)
import { interpolateRainbow, interpolateSinebow } from 'd3-scale-chromatic'

export const cyclicalColorInterpolators = {
    rainbow: interpolateRainbow,
    sinebow: interpolateSinebow,
}

export type CyclicalColorInterpolatorId = keyof typeof cyclicalColorInterpolators
import { divergingColorInterpolators, DivergingColorInterpolatorId } from './diverging'
import { sequentialColorInterpolators, SequentialColorInterpolatorId } from './sequential'
import { cyclicalColorInterpolators, CyclicalColorInterpolatorId } from './cyclical'

export const colorInterpolators = {
    ...divergingColorInterpolators,
    ...sequentialColorInterpolators,
    ...cyclicalColorInterpolators,
}

export type ColorInterpolatorId =
    | DivergingColorInterpolatorId
    | SequentialColorInterpolatorId
    | CyclicalColorInterpolatorId

export const colorInterpolatorIds = Object.keys(colorInterpolators) as ColorInterpolatorId[]
import { useMemo } from 'react'
import { get, isPlainObject } from 'lodash'
import { rgb, RGBColor } from 'd3-color'
import { Theme } from '@nivo/core'

export type ColorModifierBrightness = ['brighter', number]

export type ColorModifierDarkness = ['darker', number]

export type ColorModifierOpacity = ['opacity', number]

export type ColorModifier = ColorModifierBrightness | ColorModifierDarkness | ColorModifierOpacity

export type ColorModifierFunction = (color: RGBColor) => RGBColor

export type InheritedColorConfigStaticColor = string

export type InheritedColorConfigCustomFunction<Datum> = (d: Datum, ...drest: Datum[]) => string

export interface InheritedColorConfigFromTheme {
    theme: string
}

export interface InheritedColorConfigFromContext {
    from: string
    modifiers?: ColorModifier[]
}

export type InheritedColorConfig<Datum> =
    | InheritedColorConfigStaticColor
    | InheritedColorConfigCustomFunction<Datum>
    | InheritedColorConfigFromTheme
    | InheritedColorConfigFromContext

const isInheritedColorConfigFromTheme = <Datum>(
    config: InheritedColorConfig<Datum>
): config is InheritedColorConfigFromTheme => {
    return (config as InheritedColorConfigFromTheme).theme !== undefined
}

const isInheritedColorConfigFromContext = <Datum>(
    config: InheritedColorConfig<Datum>
): config is InheritedColorConfigFromContext => {
    return (config as InheritedColorConfigFromContext).from !== undefined
}

/**
 * Create a color generator for items which
 * might inherit from parent context,
 * for example labels, outlinesâ€¦
 *
 * Support the following strategies:
 * - custom function
 * - color from theme
 * - color from parent, with optional color modifiers
 * - static color
 */
export const getInheritedColorGenerator = <Datum = any>(
    config: InheritedColorConfig<Datum>,
    theme?: Theme
) => {
    // user provided function
    if (typeof config === 'function') {
        return config
    }

    if (isPlainObject(config)) {
        // use color from theme
        if (isInheritedColorConfigFromTheme(config)) {
            if (theme === undefined) {
                throw new Error(`Unable to use color from theme as no theme was provided`)
            }

            const themeColor = get(theme, config.theme)
            if (themeColor === undefined) {
                throw new Error(`Color from theme is undefined at path: '${config.theme}'`)
            }

            return () => themeColor
        }

        // use color from parent with optional color modifiers
        if (isInheritedColorConfigFromContext(config)) {
            const getColor = (d: Datum) => get(d, config.from)

            if (Array.isArray(config.modifiers)) {
                const modifiers: ColorModifierFunction[] = []
                for (const modifier of config.modifiers) {
                    const [modifierType, amount] = modifier
                    if (modifierType === 'brighter') {
                        modifiers.push(color => color.brighter(amount))
                    } else if (modifierType === 'darker') {
                        modifiers.push(color => color.darker(amount))
                    } else if (modifierType === 'opacity') {
                        modifiers.push(color => {
                            color.opacity = amount

                            return color
                        })
                    } else {
                        throw new Error(
                            `Invalid color modifier: '${modifierType}', must be one of: 'brighter', 'darker', 'opacity'`
                        )
                    }
                }

                if (modifiers.length === 0) return getColor

                return (datum: Datum) =>
                    modifiers
                        .reduce((color, modify) => modify(color), rgb(getColor(datum)))
                        .toString()
            }

            // no modifier
            return getColor
        }

        throw new Error(
            `Invalid color spec, you should either specify 'theme' or 'from' when using a config object`
        )
    }

    // use provided color statically
    return () => config as string
}

export const useInheritedColor = <Datum = any>(
    config: InheritedColorConfig<Datum>,
    theme?: Theme
) => useMemo(() => getInheritedColorGenerator<Datum>(config, theme), [config, theme])
import { spring, SpringHelperConfig } from 'react-motion'
import { rgb } from 'd3-color'

/**
 * Decompose a color to be used with react-motion.
 *
 * @deprecated we should use `react-spring` which supports color interpolation natively.
 */
export const interpolateColor = (color: string, springConfig?: SpringHelperConfig) => {
    const colorComponents = rgb(color)

    if (!springConfig) {
        return {
            colorR: colorComponents.r,
            colorG: colorComponents.g,
            colorB: colorComponents.b,
        }
    }

    const configWithPrecision = {
        ...springConfig,
        precision: 1,
    }

    return {
        colorR: spring(colorComponents.r, configWithPrecision),
        colorG: spring(colorComponents.g, configWithPrecision),
        colorB: spring(colorComponents.b, configWithPrecision),
    }
}

