import { Form, Button, Card, App } from "react-bootstrap";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import React from "react";
import LocalStorageService from "../../service/LocalStorageService";
import axios from "axios";
import Dashboard from "../Dashboard";

export default function LoginForm(props) {
  const [credentials, setCredentials] = useState({});
  const history = useHistory();

  const handleChange = (event) => {
    setCredentials({
      ...credentials,
      [event.target.name]: event.target.value,
    });
  };

  const signIn = async () => {
    const response = await axios.post("http://localhost:8080/auth/signin", {
      username: credentials.username,
      password: credentials.password,
    });

    if (response) {
      if (false) {
        //ReactDOM.render(modal,document.getElementById("root"));
      } else {
        LocalStorageService.setToken(response.data.token);
        LocalStorageService.setUser(response.data.username);
        LocalStorageService.setId(response.data.id);
        LocalStorageService.setRole(response.data.roles);

        history.push(<Dashboard />);
      }
    }
  };
  return (
    <Form>
      <div>
        <label
          className="display-6 m-2 justify-content-center"
          style={{ alignContent: "auto", color: "GrayText" }}
        >
          LOGIN
        </label>
      </div>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Id</Form.Label>
        <Form.Control
          type="id"
          placeholder="Enter your id"
          name="id"
          onChange={handleChange()}
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control
          type="password"
          placeholder="Password"
          name="password"
          onChange={handleChange()}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicCheckbox">
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group>
      <div className="d-flex justify-content-between">
        <div className="col-6">
          <Card
            className="button p-2"
            style={{ backgroundColor: "#a3a9f5" }}
            type="submit"
            onClick={signIn}
          >
            Submit
          </Card>
        </div>
        <div className="col-6">
          <Card
            className=" button  ml-auto p-2"
            style={{ backgroundColor: "#a3a9f5" }}
            type="forgotPassword"
          >
            Forgot Password
          </Card>
        </div>
      </div>
    </Form>
  );
}
