import * as ReactDOM from "react-dom";
import {useNavigate} from "react-router";
import InfoBox from "./InfoBox";
import CoursePage from "./CoursePage";
import {Redirect, Route} from "react-router-dom";

const StudentList=(props)=>
{


    const handleShow =(id)=>
    {
        return <Redirect to={<InfoBox id={id}/>}/>
    }

    return(
        <Router>
            <Switch>
                <Route path="/show">
                    <CoursePage/>
                </Route>
            </Switch>

        <table>
            <thead>
            <tr>
                <th> Name</th>
                <th> Surname</th>
                <th> ID Number</th>
                <th>Risk Status</th>
            </tr>
            </thead>
            <tbody>
            {props.data &&
            props.data.map((student) => {
                return (
                    <>
                        <tr>
                            <td>{student.name}</td>
                            <td>{student.surname}</td>
                            <td>{student.id}</td>
                            <td>{student.allowedOnCampus}</td>
                            <td><button onClick={()=>handleShow(student.id)} >show</button> </td>
                        </tr>
                    </>
                )
            })}
            </tbody>
        </table>
        </Router>
    )

}
export default StudentList
