import { VoronoiDomain, VoronoiLayer } from './types'

export const defaultVoronoiProps = {
    xDomain: [0, 1] as VoronoiDomain,
    yDomain: [0, 1] as VoronoiDomain,
    layers: ['links', 'cells', 'points', 'bounds'] as VoronoiLayer[],
    enableLinks: false,
    linkLineWidth: 1,
    linkLineColor: '#bbbbbb',
    enableCells: true,
    cellLineWidth: 2,
    cellLineColor: '#000000',
    enablePoints: true,
    pointSize: 4,
    pointColor: '#666666',
    role: 'img',
}
import { Delaunay } from 'd3-delaunay'

type NumberPropertyNames<T> = {
    [K in keyof T]: T[K] extends number ? K : never
}[keyof T]

export type XYAccessor<Datum> = NumberPropertyNames<Datum> | ((datum: Datum) => number)

const getAccessor = <Datum>(directive: XYAccessor<Datum>) =>
    typeof directive === 'function' ? directive : (datum: Datum) => datum[directive]

/**
 * The delaunay generator requires an array
 * where each point is defined as an array
 * of 2 elements: [x: number, y: number].
 *
 * Points represent the raw input data
 * and x/y represent accessors to x & y.
 */
export const computeMeshPoints = <Datum>({
    points,
    x = 'x' as NumberPropertyNames<Datum>,
    y = 'y' as NumberPropertyNames<Datum>,
}: {
    points: Datum[]
    x?: XYAccessor<Datum>
    y?: XYAccessor<Datum>
}): [number, number][] => {
    const getX = getAccessor<Datum>(x)
    const getY = getAccessor<Datum>(y)

    return points.map(point => [getX(point) as number, getY(point) as number])
}

export const computeMesh = ({
    points,
    width,
    height,
    debug,
}: {
    points: [number, number][]
    width: number
    height: number
    debug?: boolean
}) => {
    const delaunay = Delaunay.from(points)
    const voronoi = debug ? delaunay.voronoi([0, 0, width, height]) : undefined

    return { delaunay, voronoi }
}
import { useMemo } from 'react'
import { scaleLinear } from 'd3-scale'
import { Delaunay } from 'd3-delaunay'
import { computeMeshPoints, computeMesh, XYAccessor } from './computeMesh'
import { VoronoiCommonProps, VoronoiDatum, VoronoiCustomLayerProps } from './types'

export const useVoronoiMesh = <Datum>({
    points,
    x,
    y,
    width,
    height,
    debug,
}: {
    points: Datum[]
    x?: XYAccessor<Datum>
    y?: XYAccessor<Datum>
    width: number
    height: number
    debug?: boolean
}) => {
    const points2d = useMemo(() => computeMeshPoints<Datum>({ points, x, y }), [points, x, y])

    return useMemo(
        () => computeMesh({ points: points2d, width, height, debug }),
        [points2d, width, height, debug]
    )
}

export const useVoronoi = ({
    data,
    width,
    height,
    xDomain,
    yDomain,
}: {
    data: VoronoiDatum[]
    width: number
    height: number
    xDomain: VoronoiCommonProps['xDomain']
    yDomain: VoronoiCommonProps['yDomain']
}) => {
    const xScale = useMemo(() => scaleLinear().domain(xDomain).range([0, width]), [xDomain, width])
    const yScale = useMemo(
        () => scaleLinear().domain(yDomain).range([0, height]),
        [yDomain, height]
    )

    const points = useMemo(
        () =>
            data.map(d => ({
                x: xScale(d.x),
                y: yScale(d.y),
                data: d,
            })),
        [data, xScale, yScale]
    )

    return useMemo(() => {
        const delaunay = Delaunay.from(points.map(p => [p.x, p.y]))
        const voronoi = delaunay.voronoi([0, 0, width, height])

        return {
            points,
            delaunay,
            voronoi,
        }
    }, [points, width, height])
}

/**
 * Memoize the context to pass to custom layers.
 */
export const useVoronoiLayerContext = ({
    points,
    delaunay,
    voronoi,
}: VoronoiCustomLayerProps): VoronoiCustomLayerProps =>
    useMemo(
        () => ({
            points,
            delaunay,
            voronoi,
        }),
        [points, delaunay, voronoi]
    )
import { createElement, Fragment, ReactNode } from 'react'
import { Container, SvgWrapper, useDimensions } from '@nivo/core'
import { VoronoiSvgProps, VoronoiLayerId } from './types'
import { defaultVoronoiProps } from './props'
import { useVoronoi, useVoronoiLayerContext } from './hooks'

type InnerVoronoiProps = Partial<Omit<VoronoiSvgProps, 'data' | 'width' | 'height'>> &
    Pick<VoronoiSvgProps, 'data' | 'width' | 'height'>

const InnerVoronoi = ({
    data,
    width,
    height,
    margin: partialMargin,
    layers = defaultVoronoiProps.layers,
    xDomain = defaultVoronoiProps.xDomain,
    yDomain = defaultVoronoiProps.yDomain,
    enableLinks = defaultVoronoiProps.enableLinks,
    linkLineWidth = defaultVoronoiProps.linkLineWidth,
    linkLineColor = defaultVoronoiProps.linkLineColor,
    enableCells = defaultVoronoiProps.enableCells,
    cellLineWidth = defaultVoronoiProps.cellLineWidth,
    cellLineColor = defaultVoronoiProps.cellLineColor,
    enablePoints = defaultVoronoiProps.enableCells,
    pointSize = defaultVoronoiProps.pointSize,
    pointColor = defaultVoronoiProps.pointColor,
    role = defaultVoronoiProps.role,
}: InnerVoronoiProps) => {
    const { outerWidth, outerHeight, margin, innerWidth, innerHeight } = useDimensions(
        width,
        height,
        partialMargin
    )

    const { points, delaunay, voronoi } = useVoronoi({
        data,
        width: innerWidth,
        height: innerHeight,
        xDomain,
        yDomain,
    })

    const layerById: Record<VoronoiLayerId, ReactNode> = {
        links: null,
        cells: null,
        points: null,
        bounds: null,
    }

    if (enableLinks && layers.includes('links')) {
        layerById.links = (
            <path
                key="links"
                stroke={linkLineColor}
                strokeWidth={linkLineWidth}
                fill="none"
                d={delaunay.render()}
            />
        )
    }

    if (enableCells && layers.includes('cells')) {
        layerById.cells = (
            <path
                key="cells"
                d={voronoi.render()}
                fill="none"
                stroke={cellLineColor}
                strokeWidth={cellLineWidth}
            />
        )
    }

    if (enablePoints && layers.includes('points')) {
        layerById.points = (
            <path
                key="points"
                stroke="none"
                fill={pointColor}
                d={delaunay.renderPoints(undefined, pointSize / 2)}
            />
        )
    }

    if (layers.includes('bounds')) {
        layerById.bounds = (
            <path
                key="bounds"
                fill="none"
                stroke={cellLineColor}
                strokeWidth={cellLineWidth}
                d={voronoi.renderBounds()}
            />
        )
    }

    const layerContext = useVoronoiLayerContext({
        points,
        delaunay,
        voronoi,
    })

    return (
        <SvgWrapper width={outerWidth} height={outerHeight} margin={margin} role={role}>
            {layers.map((layer, i) => {
                if (layerById[layer as VoronoiLayerId] !== undefined) {
                    return layerById[layer as VoronoiLayerId]
                }

                if (typeof layer === 'function') {
                    return <Fragment key={i}>{createElement(layer, layerContext)}</Fragment>
                }

                return null
            })}
        </SvgWrapper>
    )
}

export const Voronoi = ({
    theme,
    ...otherProps
}: Partial<Omit<VoronoiSvgProps, 'data' | 'width' | 'height'>> &
    Pick<VoronoiSvgProps, 'data' | 'width' | 'height'>) => (
    <Container isInteractive={false} animate={false} theme={theme}>
        <InnerVoronoi {...otherProps} />
    </Container>
)
import { ResponsiveWrapper } from '@nivo/core'
import { VoronoiSvgProps } from './types'
import { Voronoi } from './Voronoi'

type ResponsiveVoronoiProps = Partial<Omit<VoronoiSvgProps, 'data' | 'width' | 'height'>> &
    Pick<VoronoiSvgProps, 'data'>

export const ResponsiveVoronoi = (props: ResponsiveVoronoiProps) => (
    <ResponsiveWrapper>
        {({ width, height }: { width: number; height: number }) => (
            <Voronoi width={width} height={height} {...props} />
        )}
    </ResponsiveWrapper>
)
import { useRef, useState, useCallback, useMemo } from 'react'
import * as React from 'react'
import { getRelativeCursor } from '@nivo/core'
import { useVoronoiMesh } from './hooks'
import { XYAccessor } from './computeMesh'

type MouseHandler<Datum> = (datum: Datum, event: React.MouseEvent) => void

interface MeshProps<Datum> {
    nodes: Datum[]
    width: number
    height: number
    x?: XYAccessor<Datum>
    y?: XYAccessor<Datum>
    onMouseEnter?: MouseHandler<Datum>
    onMouseMove?: MouseHandler<Datum>
    onMouseLeave?: MouseHandler<Datum>
    onClick?: MouseHandler<Datum>
    debug?: boolean
}

export const Mesh = <Datum,>({
    nodes,
    width,
    height,
    x,
    y,
    onMouseEnter,
    onMouseMove,
    onMouseLeave,
    onClick,
    debug,
}: MeshProps<Datum>) => {
    const elementRef = useRef<SVGGElement>(null)
    const [currentIndex, setCurrentIndex] = useState<number | null>(null)

    const { delaunay, voronoi } = useVoronoiMesh({
        points: nodes,
        x,
        y,
        width,
        height,
        debug,
    })

    const voronoiPath = useMemo(() => {
        if (debug && voronoi) {
            return voronoi.render()
        }

        return undefined
    }, [debug, voronoi])

    const getIndexAndNodeFromEvent = useCallback(
        event => {
            if (!elementRef.current) {
                return [null, null]
            }

            const [x, y] = getRelativeCursor(elementRef.current, event)
            const index = delaunay.find(x, y)

            return [index, index !== undefined ? nodes[index] : null] as [number, Datum | null]
        },
        [elementRef, delaunay]
    )

    const handleMouseEnter = useCallback(
        (event: React.MouseEvent) => {
            const [index, node] = getIndexAndNodeFromEvent(event)
            setCurrentIndex(index)
            if (node) {
                onMouseEnter?.(node, event)
            }
        },
        [getIndexAndNodeFromEvent, setCurrentIndex, onMouseEnter]
    )

    const handleMouseMove = useCallback(
        (event: React.MouseEvent) => {
            const [index, node] = getIndexAndNodeFromEvent(event)
            setCurrentIndex(index)
            if (node) {
                onMouseMove?.(node, event)
            }
        },
        [getIndexAndNodeFromEvent, setCurrentIndex, onMouseMove]
    )

    const handleMouseLeave = useCallback(
        (event: React.MouseEvent) => {
            setCurrentIndex(null)
            if (onMouseLeave) {
                let previousNode: Datum | undefined = undefined
                if (currentIndex !== null) {
                    previousNode = nodes[currentIndex]
                }
                previousNode && onMouseLeave(previousNode, event)
            }
        },
        [setCurrentIndex, currentIndex, onMouseLeave, nodes]
    )

    const handleClick = useCallback(
        (event: React.MouseEvent) => {
            const [index, node] = getIndexAndNodeFromEvent(event)
            setCurrentIndex(index)
            if (node) {
                onClick?.(node, event)
            }
        },
        [getIndexAndNodeFromEvent, setCurrentIndex, onClick]
    )

    return (
        <g ref={elementRef}>
            {debug && voronoi && (
                <>
                    <path d={voronoiPath} stroke="red" strokeWidth={1} opacity={0.75} />
                    {/* highlight current cell */}
                    {currentIndex !== null && (
                        <path fill="pink" opacity={0.35} d={voronoi.renderCell(currentIndex)} />
                    )}
                </>
            )}
            {/* transparent rect to intercept mouse events */}
            <rect
                width={width}
                height={height}
                fill="red"
                opacity={0}
                style={{ cursor: 'auto' }}
                onMouseEnter={handleMouseEnter}
                onMouseMove={handleMouseMove}
                onMouseLeave={handleMouseLeave}
                onClick={handleClick}
            />
        </g>
    )
}
import { Delaunay, Voronoi } from 'd3-delaunay'

export const renderVoronoiToCanvas = (
    ctx: CanvasRenderingContext2D,
    voronoi: Voronoi<Delaunay.Point>
) => {
    ctx.save()

    ctx.globalAlpha = 0.75
    ctx.beginPath()
    voronoi.render(ctx)
    ctx.strokeStyle = 'red'
    ctx.lineWidth = 1
    ctx.stroke()

    ctx.restore()
}

export const renderVoronoiCellToCanvas = (
    ctx: CanvasRenderingContext2D,
    voronoi: Voronoi<Delaunay.Point>,
    index: number
) => {
    ctx.save()

    ctx.globalAlpha = 0.35
    ctx.beginPath()
    voronoi.renderCell(index, ctx)
    ctx.fillStyle = 'red'
    ctx.fill()

    ctx.restore()
}
