import * as ReactDOM from "react-dom";
import InfoBox from "./InfoBox";
import { useState } from "react";

const StudentList = (props) => {
  const [riskColor, setColor] = useState(false);
  const handleShow = (id) => {
    const path = "/student-info/".concat(id);
    console.log(path);
    //history.back()
    ReactDOM.render(<InfoBox id={id} />, document.getElementById("root"));
  };

  const students = [
    {
      name: "Yağmur",
      surname: "Eryılmaz",
      id: "21903224",
      riskStatus: "risky",
    },
    {
      name: "Elif",
      surname: "Çenesiz",
      id: "21902432",
      riskStatus: "riskless",
    },
  ];

  const checkRiskStatus = () => {
    let temp;
    let bgRisky = "#f0bac1";
    let bgRiskless = "#caf5d0";
    if (students.riskStatus === "risky") {
      setColor(bgRisky);
      temp = false;
    } else {
      setColor(bgRiskless);
      temp = true;
    }
    return temp;
  };

  return (
    <table
      className="border"
      style={{ position: "relative", width: "100%", marginLeft: "0px" }}
    >
      <thead>
        <tr>
          <th> Name</th>
          <th> Surname</th>
          <th> ID Number</th>
          <th>Risk Status</th>
          <th>Student Info</th>
        </tr>
      </thead>
      <tbody>
        {students &&
          students.map((student) => {
            return (
              <>
                <tr>
                  <td>{student.name}</td>
                  <td>{student.surname}</td>
                  <td>{student.id}</td>
                  {{ checkRiskStatus }}? (
                  <td style={{ backgroundColor: riskColor }}>
                    {student.riskStatus}
                  </td>
                  ) : (
                  <td style={{ backgroundColor: riskColor }}>
                    {student.riskStatus}
                  </td>
                  )}
                  <td>
                    <button
                      style={{ width: "100%" }}
                      className="button"
                      onClick={() => handleShow(student.id)}
                    >
                      show
                    </button>{" "}
                  </td>
                </tr>
              </>
            );
          })}
      </tbody>
    </table>
  );
};
export default StudentList;
