import React, { useState } from "react";
import StudentService from "../../service/StudentService";
import { Form } from "react-bootstrap";

const ReservationCheck = () => {
  const [studentId, setStudentId] = useState("");
  const [info, setInfo] = useState("");

  const handleChange = (event) => {
    setStudentId(event.target.value);
  };

  const getData = async () => {
    console.log(studentId);

    const students = await StudentService.getStudentById(studentId);
    const hesCode = students[0].hesCode.toString();
    console.log(hesCode);
    const response = await StudentService.validateHesCode(
      hesCode,
      "25856242442",
      "Ge2212690"
    );
    const split = response.toString().split("\n");
    setInfo(split);
  };

  const studentIdForm = (
    <div>
      <Form>
        <div className="row d-flex">
          <div className="col-8">
            <label className="" style={{ color: "indigo200" }}>
              Email address:
            </label>
            <input
              type="text"
              id="studentId"
              name="studentId"
              placeholder="student ID"
              onChange={() => handleChange}
            ></input>
            <button
              className="button m-0 p-2"
              value="Check Reservation"
              onClick={getData}
            >
              Check Reservation
            </button>
          </div>
        </div>
      </Form>
    </div>
  );
  return (
    <div>
      <div>{studentIdForm}</div>
      <div>{info}</div>
    </div>
  );
};

export default ReservationCheck;
