import { Form, Button, Card, App } from "react-bootstrap";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import React from "react";
import LocalStorageService from "../../../service/LocalStorageService";
import axios from "axios";
import Dashboard from "../../Dashboard";
import ForgotPasswordForm from "./forgotPasswordForm";

export default function LoginForm(props) {
  const [credentials, setCredentials] = useState({});
  const history = useHistory();

  const handleChange = (event) => {
    setCredentials({
      ...credentials,
      [event.target.name]: event.target.value,
    });
  };

  const forgotPassword = () => {
    return (
      <div
        class="modal fade"
        id="exampleModalCenter"
        tabindex="-1"
        role="dialog"
        aria-labelledby="exampleModalCenterTitle"
        aria-hidden="true"
      >
        <div class="modal-dialog modal-dialog-centered" role="document">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalCenterTitle">
                Modal title
              </h5>
              <button
                type="button"
                class="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div class="modal-body">...</div>
            <div class="modal-footer">
              <button
                type="button"
                class="btn btn-secondary"
                data-dismiss="modal"
              >
                Close
              </button>
              <button type="button" class="btn btn-primary">
                Save changes
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };
  const signIn = async () => {
    let path = `home`;
    history.push(path);
  };

  return (
    <Form>
      <div>
        <label
          className="display-6 m-2 justify-content-center"
          style={{ alignContent: "auto", color: "GrayText" }}
        >
          LOGIN
        </label>
      </div>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Id</Form.Label>
        <Form.Control
          type="id"
          placeholder="Enter your id"
          name="id"
          //onChange={handleChange()}
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword" hidden={true}>
        <Form.Label>Password</Form.Label>
        <Form.Control
          type="password"
          placeholder="Password"
          name="password"
          //onChange={handleChange()}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicCheckbox">
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group>
      <div className="d-flex justify-content-between">
        <div className="col-6">
          <Card
            className="button p-2"
            style={{ backgroundColor: "#a3a9f5" }}
            type="submit"
            onClick={signIn}
          >
            Submit
          </Card>
        </div>
        <div className="col-6">
          <Card
            className=" button  ml-auto p-2"
            style={{ backgroundColor: "#a3a9f5" }}
            type="forgotPassword"
            data-toggle="modal"
            data-target="#exampleModalCenter"
            onClick={forgotPassword}
          >
            Forgot Password
          </Card>
        </div>
      </div>
    </Form>
  );
}
