import React from "react";

class SeatingPlan extends React.Component
{
    constructor(props) {
        super(props);
        this.state = {
            chart :[],
            data: [21902461, 21902444, "ccc", "ddd", 2190222,"", "xxx"],
            rowNo: 2,
            columnNo: 4,
            readOnly:false
        }
    }

    saveStudent = (event) => {
        let seatId = event.target.id;
        let studentId = event.target.value;
        let temp = this.state.data;
        temp[seatId] = studentId;
        this.setState({ data: temp });
    };

    fetchSeatingPlan=()=>
    {
        let table = [];
        let parent = [];
        for (let i = 0; i <= 3; i++)
        {
            let children = [];
            for (let j = 0; j <= 2; j++)
            {
                let id = j+(i*2);
                if (j === 0)
                    children.push(<td> <label type="text" name="seat" /> {i}</td>)
                if (i === 0)
                    children.push(<td> <label type="text" name="seat" /> {String.fromCharCode(j+97)}</td>)
                else
                    children.push(<td>
                        <input type="text" name="seat" id = {id}
                               onChange={this.saveStudent} readOnly={this.state.readOnly} value={this.state.data[id]} contentEditable={false}>

                        </input> </td>);
            }
            parent.push(<tr> {children} </tr>);
        }
        table.push(<table>{parent}</table>)

        this.setState({chart:table});
    }

    componentDidMount() {
        this.fetchSeatingPlan();
    }

    updateChart(){
    this.setState({readOnly:false})
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name]: event.target.value,
        });
    };

    render()
    {
        return(

            <div className="seatingPlanForm">
                {this.state.chart}
                <button id = "seatingPlanUpdate" onClick={this.updateChart}> Update the seating plan </button>
            </div>
        )
    }
}

export default SeatingPlan;
