import StudentService from "./StudentService";

const {useState} = require("react");

const UpdateHesCodeBox =(props)=> {

    const [hesCode, setHesCode] = useState(props.hesCode);

    const handleChange=(event)=>
    {
        setHesCode(event.target.value);
    }

    const changeHesCode=async () => {
        await StudentService.updateHesCode(props.id, props.hesCode)
        console.log("hello")
    }

    return(
        <form>
            <label> Enter your HES code</label>
            <input type="text" id="hesCode" name="hesCode" placeholder={props.hesCode}
                   value={hesCode} onChange={handleChange} />
            <input type="submit" value="Update" onClick={()=>changeHesCode()} />
        </form>
    )
}
export default UpdateHesCodeBox
