import React from "react";
import "./LoginFormStyle.css";
import StudentInfoBox from "./StudentInfoBox";

class ReservationCheck extends React.Component
{
    constructor(props) {
        super(props);
        this.state = {
            id:"",
            date:"29.11.2021",
            time:"14.30",
            place:"Library Study Room 3",
            risky: "",
            clicked: false

        }
    }




    handleChange = (event) => {

        this.setState({id: event.target.value
        });
    };


    clicked = (event) => {

        this.setState({clicked: true})

    };

    shouldComponentUpdate(nextProps, nextState, nextContext) {
        return this.state.clicked
    }


    render() {
        var studentIdForm =
            <>
                <label></label>
                <input type="text" id="studentId" name="studentId" placeholder="student ID"
                       onChange={this.handleChange}  ></input>
                <button  value="Check Reservation" onClick={this.clicked} >Check Reservation</button>
            </>



        return (
                <div>
                    {studentIdForm}
                    <StudentInfoBox id = {this.state.id} date = {this.state.date} time = {this.state.time}
                                    place = {this.state.place}  risky = {this.state.risky} clicked = {this.state.clicked}  />
                </div>

        );
    }
            }
            export default ReservationCheck;
