import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import InfoBox from "./profileComponents/InfoBox";
import React from "react";
import { Navigation } from "react-minimal-side-navigation";
import { FaUserAlt, FaBook } from "react-icons/fa";
import { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import "react-minimal-side-navigation/lib/ReactMinimalSideNavigation.css";
import CourseService from "../../service/CourseService";
import CoursePage from "../pages/CoursePage";

const SideBar = (props) => {
  const history = useHistory();
  let subNavArr = [];
  useEffect(() => {
    createSubNav();
  }, []);

  const [courses, setCourses] = useState([]);

  const fetchData = async () => {
    let list = await CourseService.getAllCourses();
    setCourses(list);
  };

  useEffect(() => {
    fetchData().then();
  }, []);

  const createSubNav = () => {
    courses.forEach((c) => subNavArr.push({ title: c.name, itemId: c.name }));
  };
  return (
    <div>
      <>
        <div className="">
          <div className="col-3 justify-content-start">
            <ul className="side " style={{ margin: "1" }}>
              <Navigation
                // you can use your own router's api to get pathname
                activeItemId="/management/members"
                onSelect={() => {
                  {
                    courses.map((course) => {
                      return (
                        <Route path={"/course/".concat(course.courseCode)}>
                          <MenuTab />
                          <CoursePage />
                        </Route>
                      );
                    });
                  }
                }}
                items={[
                  {
                    title: "Profile Info",
                    itemId: "/dashboard",
                    // you can use your own custom Icon component as well
                    // icon is optional
                    elemBefore: () => <FaUserAlt />,
                  },
                  {
                    title: "Courses",
                    itemId: "/management",
                    elemBefore: () => <FaBook />,
                    subNav: subNavArr,
                  },
                ]}
              />
            </ul>
          </div>
        </div>
      </>
    </div>
  );
};

export default SideBar;
