import StudentService from "./StudentService";

class InfoBox extends React.Component{


     constructor(props) {
        super(props);
        this.state = {
            student: ''
        }
    }
    updateHesCode=async ()=>
    {
        console.log("hello")
    }

    async componentDidMount() {
         console.log("didMount")
        this.setState({student: await StudentService.getStudentById(this.props.id)})
        console.log(this.state.student)
    }


    render(){

        return (
            <>
                <label className='infoBox'> Name: {this.state.student.name} </label>
                <br/>
                {
                this.state.student.email && this.state.student.email.map((email)=>{return<label className='infoBox'> Email: {this.state.student.email} </label>})

            }

                <br/>
                <label className='infoBox'>
                    Hes Code: {this.state.student.hesCode}
                    <button onClick={this.updateHesCode}> Update </button>
                </label>
                <></>
            </>
        );
    }

}

export default InfoBox
